import React from "react"
import "./Stylesheets/form.css"
import formBg from "../Assets/form_background.png"

export default class FormToReg extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            identityName: null,
            identityClass: null,
            identityYear: null,
            anonName: false,
            anonClass: false,
            anonYear: false,

        }
        this.years = ["2016-2017", "2017-2018", "2018-2019", "2019-2020", "2020-2021", "2021-2022"]

    }

    render() {
        return (
            <div id="form">

                <form>
                    <div>
                        {/* <img src={formBg} id="form-bg"></img> */}
                    </div>
                    <div id="form-group-name">
                        <div className="checkBox-anon">
                            <input type="checkbox" id="anon-name" onChange={(e) => {
                                this.setState({ anonName: !this.state.anonName });
                                document.getElementById("nameInput").disabled = !this.state.anonName;
                            }}></input>
                            <label for="anon-name"></label>
                        </div>
                        <input type="text" placeholder="Họ và tên" id="nameInput"></input>
                    </div>
                </form>
            </div>
        )
    }

}
